        "introduction-for-users" : {"priority":6,"track":"Tech Training","moderated":false,"session":"Smart Data Models","prefix":"Training","title":"Introduction for users","date":"2001-05-21T22:00:00.000Z","start":"12:00 pm","end":"12:30 pm","location":"Room Jbel Ayachi","img":"https://www.fiware.org/fiware-summit/assets/icons/icon-tech-training.svg","description":"","excerpt":"","publish":true,"moderators":[],"speakers":[{"title":"","name":"Alberto Abella","surname":"Abella","image":"alberto-abella.jpg","company":"FIWARE Foundation","companyType":"","website":"https://www.fiware.org/","job":"Data Modeling Expert & Evangelist","bio":"Alberto Abella (m) is PhD in Business (Open data) and Telecommunications Engineer and Master in Total Quality Management, Business Administration and Business Organization. He works as a Data Modelling Expert and Technical Evangelist at FIWARE and currently is the responsible person for the smart data models initiative.","linkedIn":"https://www.linkedin.com/in/albertoabella/","twitter":"https://twitter.com/aabella","filters":["Expert","Evangelist"],"email":"","publish":true,"img":"https://www.fiware.org/wp-content/directories/people/images/200px/alberto-abella.jpg","flagUrl":"https://www.fiware.org/wp-content/directories/people/images/flag","shortJob":"Expert"}],"startTime":"2001-05-22T10:00:00.000Z","shortDate":"22 May","social":"/fgs-2025/introduction-for-users.html","socialImage":"/fgs-2025/introduction-for-users.png","trackColor":"cyan","numSpeakers":1},
