        "options-for-data-persistence" : {"priority":7,"track":"Tech Training","moderated":false,"session":"FIWARE Catalog","prefix":"Training","title":"Options for Data Persistence","date":"2001-05-22T22:00:00.000Z","start":"3:00 pm","end":"3:30 pm","location":"Room Jbel Ayachi","img":"https://www.fiware.org/fiware-summit/assets/icons/icon-dev-training.svg","description":"","excerpt":"","publish":true,"moderators":[],"speakers":[{"title":"","name":"Jason Fox","surname":"Fox","image":"jason-fox.jpg","company":"FIWARE Foundation","companyType":"","website":"https://www.fiware.org/","job":"Tech Community Manager & Evangelist","bio":"Jason Fox (m) has been working for FIWARE Foundation for the last six years in the role of technical evangelist, educating and diseminating information about NGSI interfaces, JSON-LD, Linked Data, the components within the FIWARE Catalogue and Securing FIWARE architectures. Before joining FIWARE, he worked as a software engineer for over 20 years in various roles both in Germany and the United Kingdom using a wide variety of programming languages ranging from C++ to Java to Node.js. This has included coding the software behind selling lottery tickets online for the British National Lottery, and and developing and maintaining technical documentation applications for Nokia.","linkedIn":"https://www.linkedin.com/in/jason-fox-8a79563/","twitter":"","filters":["Manager","Evangelist"],"email":"","publish":true,"img":"https://www.fiware.org/wp-content/directories/people/images/200px/jason-fox.jpg","flagUrl":"https://www.fiware.org/wp-content/directories/people/images/flag","shortJob":"Manager"}],"startTime":"2001-05-23T13:00:00.000Z","shortDate":"23 May","social":"/fgs-2025/options-for-data-persistence.html","socialImage":"/wp-content/directories/fiware-summit/social-media/images/options-for-data-persistence.png","trackColor":"light-green","numSpeakers":1},
